 $(document).ready(function(){

 $("#stockmaterialButton").on('click',function(){

    var materialdata=[];
    // loop over each table row (tr)
    $("#returnstockTable tr").each(function(){
         var currentRow=$(this);

         var col1_value=currentRow.find(".product select").val();
         var col2_value=currentRow.find(".igt_barcode input").val();
         var col3_value=currentRow.find(".oem_barcode input").val();
         var col4_value=currentRow.find(".lineid input").val();
         var obj={};
         obj.col1=col1_value;
         obj.col2=col2_value;
         obj.col3=col3_value;
         obj.col4=col4_value;

         materialdata.push(obj);

    });
  materialdictionary = JSON.stringify(materialdata); 
  var lista = document.getElementById("stockmaterial_vals")
  lista.value = materialdictionary 
  console.log(materialdata);   
  });

});


