var objective_data_vals = []
function onFormSubmit(){
   src_loc = document.getElementById('linesrclist').value;
   dest_loc = document.getElementById('linedestinationloc').value;
   if (src_loc == 'blank'){
      alert('Source Location Cannot be empty')
   }
   if (dest_loc == 'blank'){
      alert('Destination Location Cannot be empty')
   } 
   if (dest_loc != 'blank'){
      var formData = readFormData();
      insertFormRecord(formData);
      resetProductForm();
   }


}
function readFormData(){
  var formData = {};
  formData['lineproduct'] = document.getElementById('lineproduct').value;
  formData['linename'] = document.getElementById('linename').value;
  formData['lineproductuom'] = document.getElementById('lineproductuom').value;
  formData['lineproject'] = document.getElementById('lineproject').value;
  formData['linesrclist'] = document.getElementById('linesrclist').value;
  formData['linedestinationloc'] = document.getElementById('linedestinationloc').value;
  formData['linesupplier'] = document.getElementById('linesupplier').value;
  return formData;
}
function insertFormRecord(data){
   var tableproduct = document.getElementById('productTable');
   var newRow = tableproduct.insertRow(tableproduct.length)
   cell1 = newRow.insertCell(0);
   cell1.innerHTML= data.lineproduct
   cell1.classList.add("lineproduct");
   cell2 = newRow.insertCell(1);
   cell2.innerHTML= data.linename
   cell2.classList.add("linename");
   cell3 = newRow.insertCell(2);
   cell3.innerHTML= data.lineproductuom
   cell3.classList.add("lineproductuom");
   cell4 = newRow.insertCell(3);
   cell4.innerHTML= data.lineproject
   cell4.classList.add("lineproject");
   cell5 = newRow.insertCell(4);
   cell5.innerHTML= data.linesrclist
   cell5.classList.add("linesrclist");
   cell6 = newRow.insertCell(5);
   cell6.innerHTML= data.linedestinationloc
   cell6.classList.add("linedestinationloc");                                 
   cell7 = newRow.insertCell(6);
   cell7.innerHTML= data.linesupplier
   cell7.classList.add("linesupplier");                                                 
   cell8 = newRow.insertCell(7);                                   
   cell8.innerHTML= '';

}
function resetProductForm(){
  document.getElementById('lineproduct').value="";
  document.getElementById('linename').value="";
  document.getElementById('lineproductuom').value="";
  document.getElementById('lineproject').value="";   
  document.getElementById('linesupplier').value="";  
}

function onDelete(td){
      row = td.parentElement.parentElement;
      document.getElementById("productTable").deleteRow(row.rowIndex);
      resetForm();
}

