        function onFormSubmitReturn(){
           return_src_loc = document.getElementById('returnlinesrclist').value;
           return_dest_loc = document.getElementById('returnlinesrclist').value;
           if (return_src_loc == 'blank'){
              alert('Source Location Cannot be empty')
           }
           if (return_dest_loc == 'blank'){
              alert('Destination Location Cannot be empty')
           } 
           if (return_dest_loc != 'blank'){
              var formData = readFormDataReturn();
              insertFormRecordReturn(formData);
              resetForm();
           }

        }
        function readFormDataReturn(){
          var formData = {};
          formData['returnlineproduct'] = document.getElementById('returnlineproduct').value;
          formData['retunigtbarcode'] = document.getElementById('retunigtbarcode').value;
          formData['returnlineoembarcode'] = document.getElementById('returnlineoembarcode').value;
          formData['returnlinename'] = document.getElementById('returnlinename').value;
          formData['returnlineqty'] = document.getElementById('returnlineqty').value;
          formData['returnlineproject'] = document.getElementById('returnlineproject').value;
          formData['returnlinesrclist'] = document.getElementById('returnlinesrclist').value;
          formData['retunrnlinedestloc'] = document.getElementById('retunrnlinedestloc').value;
          return formData;
        }
